get '/lotto-sample' do
   @lotto = (1..45).to_a.sample(6).sort
   url = "http://www.nlotto.co.kr/common.do?method=getLottoNumber&drwNo=809"
   # Chrome 확장 프로그램 JSON View 받아서 접속하면 구분되서 보기 편함
   @lotto_info = RestClient.get(url)
   @lotto_hash=JSON.parse(@lotto_info)
   
   @winner =[]
   @lotto_hash.each do |k, v|
       if k.include?('drwtNo') 
        #배열에 저장
        @winner << v
       end
   end
   @matchnum=(@winner & @lotto).length
   @bonusnum=@lotto_hash["bnusNo"]
   @lotto.include?(@bonusnum)
   #몇등인지
   @num=1
   @score =if @matchnum ==1 
                @num="꽝"
            elsif  @matchnum ==2
            @num="꽝"
             elsif  @matchnum ==3
             @num="5등"
              elsif  @matchnum ==4
              @num="4등"
               elsif  @matchnum ==5 
               @num="3등"
               elsif  @matchnum ==5 && @lotto.include?(@bonusnum)
               @num="2등"
                elsif  @matchnum == 6
                @num="1등"
            else
               @num= "꽝"
            end
   @result = 
   case[@matchnum,@lotto.include?(@bonusnum)]
    when [6, false] then "1등"
         when [6, true] then "2등"
              when [6, false] then "3등"
                   when [6, false] then "4등"
                        when [6, false] then "5등"
                        else '꽝'
                        end

   erb :lotto
end

